import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from './Footer';

const Noticias = () => {
  const [noticias, setNoticias] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNoticias = async () => {
      try {
        const response = await axios.get('https://api.example.com/games'); // Cambia esta URL por la API pública que encuentres
        setNoticias(response.data.results); // Ajusta según la estructura de respuesta
      } catch (error) {
        setError(error.message);
      }
    };

    fetchNoticias();
  }, []);

  if (error) return <p>Error al cargar las noticias: {error}</p>;

  return (
    <div>
      <h2>Noticias de Juegos</h2>
      <ul>
        {noticias.map((noticia, index) => (
          <li key={index}>
            <h3>{noticia.title}</h3>
            <p>{noticia.description}</p>
          </li>
        ))}
      </ul>
      <Footer />
    </div>
  );
}

export default Noticias;
