import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Footer from './Footer';


const Noticias = () => {
  const [noticias, setNoticias] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNoticias = async () => {
      try {
        // RAWG API para obtener información de juegos populares (o puedes cambiar el endpoint según necesidad)
        const response = await axios.get('https://cors-anywhere.herokuapp.com/https://api.rawg.io/api/games');;
        setNoticias(response.data.results);
      } catch (err) {
        setError('Error al obtener las noticias');
      } finally {
        setLoading(false);
      }
    };

    fetchNoticias();
  }, []);

  if (loading) return <p>Cargando noticias...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Noticias</h2>
      {noticias.length === 0 ? (
        <p>No hay noticias disponibles.</p>
      ) : (
        <ul>
          {noticias.map((noticia) => (
            <li key={noticia.id}>
              <strong>{noticia.name}</strong>: {noticia.released}
            </li>
          ))}
        </ul>
      )}
      <Footer />
    </div>
  );
}

export default Noticias;
